import {  useParams } from "react-router-dom";

import EventItem from "../components/EventItem";

import { useQuery, useMutation } from "@tanstack/react-query";
import { eventDetailLoader, deleteAction } from "../util/http.js";

function EventDetailPage() {
  // const data = useRouteLoaderData('event-detail');
  const params = useParams();
  const { data, isPending, isError, error } = useQuery({
    queryKey: ["events", params.eventId],
    queryFn: ({ signal }) =>
      eventDetailLoader({ params: { eventId: params.eventId } }),
  });

  const { mutate, isPendingMutation, isErrorMutation, errorMutation } = useMutation({
    mutationFn: createEditEventAction,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["events"] });
      navigate("/events");
    },
  });

  if (isPending) return <p>Loading...</p>;
  if (isError) return <p>Error: {error.message}</p>;

  if (!data || !data.event) {
    return <p>No event found.</p>;
  }

  return (
    <EventItem
      event={data.event}
      deleteAction={deleteAction}
      eventId={params.eventId}
    />
  );
}

export default EventDetailPage;
