// import { useRouteLoaderData } from 'react-router-dom';
import { useQuery, useMutation } from "@tanstack/react-query";
import { eventDetailLoader } from "../util/http.js";

import EventForm from "../components/EventForm";
import { useParams } from "react-router-dom";

function EditEventPage() {
  // const data = useRouteLoaderData('event-detail');
  const params = useParams();
  const { data, isPending, isError, error } = useQuery({
    queryKey: ["events", params.eventId],
    queryFn: ({ signal }) =>
      eventDetailLoader({ params: { eventId: params.eventId } }),
  });

  const { mutate, isPending:isPendingMutation, isError:Mutation, error:errorMutation } = useMutation({
    mutationFn: createEditEventAction,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["events"] });
      navigate("/events");
    },
  });

  if (isPending) return <p>Loading...</p>;
  if (isError) return <p>Error: {error.message}</p>;

  if (!data || !data.event) {
    return <p>No event found.</p>;
  }

  function handleSubmit(changeformData) {
    mutate(changeformData);
  }

  return (
    <EventForm method="PATCH" event={data.event} onSubmit={handleSubmit} />
  );
}

export default EditEventPage;
